(22, 'ALAMBRE GALVANIZADO', '<p>Es un alambre brillante o negro que lleva un recubrimiento de zinc, se fabrica con diferentes capas y di&aacute;metros seg&uacute;n su uso; es muy resistente a la corrosi&oacute;n es de acero Galvanizado a bajo carbono.</p>\r\n\r\n<p>\r\n<table border="1" cellspacing="0" cellpadding="0" class="rwd-table">\r\n  <tr>\r\n    <td colspan="3"><p>ALAMBRE GALVANIZADO</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>CALIBRE BWG</p></td>\r\n    <td><p>DIAMETRO NOMINAL MM</p></td>\r\n    <td><p>METROS POR KILO APROX.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>6</p></td>\r\n    <td><p>5,16</p></td>\r\n    <td><p>6</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>8</p></td>\r\n    <td><p>4,19</p></td>\r\n    <td><p>9</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>9</p></td>\r\n    <td><p>3,76</p></td>\r\n    <td><p>11</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>10</p></td>\r\n    <td><p>3,4</p></td>\r\n    <td><p>14</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>10,5</p></td>\r\n    <td><p>3,23</p></td>\r\n    <td><p>15</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>11</p></td>\r\n    <td><p>3,05</p></td>\r\n    <td><p>17</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>12</p></td>\r\n    <td><p>2,77</p></td>\r\n    <td><p>21</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>12,5</p></td>\r\n    <td><p>2,56</p></td>\r\n    <td><p>24</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>13</p></td>\r\n    <td><p>2,41</p></td>\r\n    <td><p>28</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>13,5</p></td>\r\n    <td><p>2,26</p></td>\r\n    <td><p>31</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>14</p></td>\r\n    <td><p>2,11</p></td>\r\n    <td><p>37</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>15</p></td>\r\n    <td><p>1,83</p></td>\r\n    <td><p>50</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>16</p></td>\r\n    <td><p>1,65</p></td>\r\n    <td><p>60</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>18</p></td>\r\n    <td><p>1,24</p></td>\r\n    <td><p>104</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td><p>2